mapper.select:
SELECT `softwareName`,`APKName`,`softwareSize`, (SELECT valueName FROM `data_dictionary` d WHERE a.flatformId=d.valueId AND d.typeCode='APP_FLATFORM') AS flatformName, (SELECT categoryName FROM `app_category` c WHERE a.categoryLevel1=c.id) AS categoryLevel1Name, (SELECT categoryName FROM `app_category` c WHERE a.categoryLevel2=c.id) AS categoryLevel2Name, (SELECT categoryName FROM `app_category` c WHERE a.categoryLevel3=c.id) AS categoryLevel3Name, (SELECT valueName FROM `data_dictionary` d WHERE a.status=d.valueId AND d.typeCode='APP_STATUS') AS statusName, a.downloads, (SELECT versionNo FROM `app_version` v WHERE v.id=a.versionId) AS versionNo,a.`status` FROM `app_info` a where 1=1
and a.softwareName like concat('%',#{flatformName},'%')
and a.status=#{status}
and a.flatformId=#{flatformId}
and a.categoryLevel1=#{categoryLevel1}
and a.categoryLevel2=#{categoryLevel2}
and a.categoryLevel3=#{categoryLevel3}
LIMIT #{index},5

SELECT `softwareName`,`APKName`,`softwareSize`, (SELECT valueName FROM `data_dictionary` d WHERE a.flatformId=d.valueId AND d.typeCode='APP_FLATFORM') AS flatformName, (SELECT categoryName FROM `app_category` c WHERE a.categoryLevel1=c.id) AS categoryLevel1Name, (SELECT categoryName FROM `app_category` c WHERE a.categoryLevel2=c.id) AS categoryLevel2Name, (SELECT categoryName FROM `app_category` c WHERE a.categoryLevel3=c.id) AS categoryLevel3Name, (SELECT valueName FROM `data_dictionary` d WHERE a.status=d.valueId AND d.typeCode='APP_STATUS') AS statusName, a.downloads, (SELECT versionNo FROM `app_version` v WHERE v.id=a.versionId) AS versionNo FROM `app_info` a where 1=1
and a.softwareName like concat('%',#{flatformName},'%')
and a.status=#{status}
and a.flatformId=#{flatformId}
and a.categoryLevel1=#{categoryLevel1}
and a.categoryLevel2=#{categoryLevel2}
and a.categoryLevel3=#{categoryLevel3}
